import type React from "react"
import type { Metadata } from "next"
import { GeistSans } from "geist/font/sans"
import { GeistMono } from "geist/font/mono"
import { Analytics } from "@vercel/analytics/next"
import { Toaster } from "@/components/ui/toaster"
import { Suspense } from "react"
import { AuthProvider } from "@/contexts/auth-context"
import "./globals.css"

export const metadata: Metadata = {
  title: "CodeContest Pro",
  description: "Modern code contest platform with plagiarism detection",
  generator: "v0.app",
}

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode
}>) {
  return (
    <html lang="en">
      <body className={`font-sans ${GeistSans.variable} ${GeistMono.variable} antialiased`}>
        <AuthProvider>
          <Suspense fallback={null}>
            {children}
            <Toaster />
          </Suspense>
        </AuthProvider>
        <Analytics />
      </body>
    </html>
  )
}

backend/server.js.content:
require('dotenv').config();

const express = require('express');
const http = require('http');
const cors = require('cors');
const helmet = require('helmet');
const compression = require('compression');
const rateLimit = require('express-rate-limit');
const morgan = require('morgan');

const connectDB = require('./config/database');
const apiRoutes = require('./routes');
const LeaderboardSocket = require('./sockets/leaderboardSocket');

// Create Express app
const app = express();
const server = http.createServer(app);

// Initialize WebSocket
const leaderboardSocket = new LeaderboardSocket(server);

// Connect to MongoDB
connectDB();

// Middleware
app.use(helmet({
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'"],
      scriptSrc: ["'self'"],
      imgSrc: ["'self'", "data:", "https:"],
    },
  },
}));

app.use(compression());

app.use(cors({
  origin: process.env.WEBSOCKET_CORS_ORIGINS?.split(',') || [
    'http://localhost:3000',
    'http://localhost:5000',
    'https://codecontest.replit.app'
  ],
  credentials: true
}));

// Rate limiting
const limiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100, // limit each IP to 100 requests per windowMs
  message: {
    success: false,
    error: 'Too many requests from this IP, please try again later'
  }
});

// Apply rate limiting to all requests
app.use('/api/', limiter);

// Stricter rate limiting for auth endpoints
const authLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 5, // limit each IP to 5 requests per windowMs for auth
  message: {
    success: false,
    error: 'Too many authentication attempts, please try again later'
  }
});

app.use('/api/auth/login', authLimiter);
app.use('/api/auth/register', authLimiter);

// Logging
if (process.env.NODE_ENV === 'development') {
  app.use(morgan('dev'));
} else {
  app.use(morgan('combined'));
}

// Body parsing middleware
app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: true, limit: '10mb' }));

// Trust proxy (important for Replit)
app.set('trust proxy', 1);

// API routes
app.use('/api', apiRoutes);

// WebSocket endpoint info
app.get('/websocket', (req, res) => {
  res.json({
    success: true,
    message: 'WebSocket server is running',
    endpoints: [
      'ws://localhost:3001/contest/{contestId}/leaderboard',
      'ws://localhost:3001/contest/{contestId}/submissions',
      'ws://localhost:3001/notifications'
    ],
    events: {
      client_to_server: [
        'join_contest',
        'leave_contest',
        'join_submission_feed',
        'leave_submission_feed'
      ],
      server_to_client: [
        'leaderboard_update',
        'new_submission',
        'contest_update',
        'notification',
        'error'
      ]
    }
  });
});

// Serve uploaded files
app.use('/uploads', express.static(process.env.UPLOAD_PATH || './uploads'));

// 404 handler
app.use('*', (req, res) => {
  res.status(404).json({
    success: false,
    error: 'API endpoint not found',
    availableEndpoints: [
      '/api/auth',
      '/api/contests',
      '/api/problems',
      '/api/submissions',
      '/api/leaderboard',
      '/api/analytics',
      '/api/plagiarism',
      '/api/upload'
    ]
  });
});

// Global error handler
app.use((error, req, res, next) => {
  console.error('Global error handler:', error);

  // Mongoose validation error
  if (error.name === 'ValidationError') {
    const errors = Object.values(error.errors).map(err => ({
      field: err.path,
      message: err.message
    }));

    return res.status(400).json({
      success: false,
      error: 'Validation error',
      details: errors
    });
  }

  // MongoDB duplicate key error
  if (error.code === 11000) {
    const field = Object.keys(error.keyValue)[0];
    return res.status(400).json({
      success: false,
      error: `${field} already exists`
    });
  }

  // Multer file upload errors
  if (error instanceof require('multer').MulterError) {
    if (error.code === 'LIMIT_FILE_SIZE') {
      return res.status(400).json({
        success: false,
        error: 'File size too large'
      });
    }
    if (error.code === 'LIMIT_FILE_COUNT') {
      return res.status(400).json({
        success: false,
        error: 'Too many files'
      });
    }
  }

  // JWT errors
  if (error.name === 'JsonWebTokenError') {
    return res.status(401).json({
      success: false,
      error: 'Invalid token'
    });
  }

  if (error.name === 'TokenExpiredError') {
    return res.status(401).json({
      success: false,
      error: 'Token expired'
    });
  }

  // Default error response
  res.status(error.status || 500).json({
    success: false,
    error: process.env.NODE_ENV === 'development' ? 
      error.message : 'Internal server error'
  });
});

// Start server
const PORT = process.env.PORT || 3001;

server.listen(PORT, '0.0.0.0', () => {
  console.log(` Server running on port ${PORT}`);
  console.log(` API available at http://localhost:${PORT}/api`);
  console.log(` WebSocket server running on port ${PORT}`);
  console.log(` File uploads directory: ${process.env.UPLOAD_PATH || './uploads'}`);
  console.log(` Environment: ${process.env.NODE_ENV || 'development'}`);
});

// Export for testing or external use
module.exports = { app, server, leaderboardSocket };

components/button-loading.tsx.content:
"use client"

import { Button } from "@/components/ui/button"
import { LoadingSpinner } from "@/components/loading-spinner"
import { cn } from "@/lib/utils"
import { ReactNode } from "react"

interface ButtonLoadingProps {
  loading?: boolean
  children: ReactNode
  loadingText?: string
  className?: string
  [key: string]: any
}

export function ButtonLoading({
  loading = false,
  children,
  loadingText,
  className,
  ...props
}: ButtonLoadingProps) {
  return (
    <Button 
      className={cn(className)} 
      disabled={loading || props.disabled}
      {...props}
    >
      {loading && <LoadingSpinner size="sm" className="mr-2" />}
      {loading ? (loadingText || "Loading...") : children}
    </Button>
  )
}

components/contest-card.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Progress } from "@/components/ui/progress"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Users, Clock, Trophy, Timer } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { useRouter } from "next/navigation"

interface Contest {
  id: number
  title: string
  description: string
  duration: string
  participants: number
  maxParticipants?: number
  status: "upcoming" | "active" | "ended"
  startTime?: string
  endTime?: string
  difficulty: "Easy" | "Medium" | "Hard" | "Expert"
  prize?: string
}

interface ContestCardProps {
  contest: Contest
}

export function ContestCard({ contest }: ContestCardProps) {
  const [timeLeft, setTimeLeft] = useState<string>("")
  const [progress, setProgress] = useState<number>(0)
  const [isJoined, setIsJoined] = useState<boolean>(false)
  const { toast } = useToast()
  const router = useRouter()

  useEffect(() => {
    if (contest.status === "active" && contest.endTime) {
      const interval = setInterval(() => {
        const now = new Date().getTime()
        const end = new Date(contest.endTime!).getTime()
        const distance = end - now

        if (distance > 0) {
          const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))
          const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60))
          const seconds = Math.floor((distance % (1000 * 60)) / 1000)

          setTimeLeft(`${hours}h ${minutes}m ${seconds}s`)

          // Calculate progress (assuming 3 hour contest)
          const totalDuration = 3 * 60 * 60 * 1000 // 3 hours in ms
          const elapsed = totalDuration - distance
          setProgress((elapsed / totalDuration) * 100)
        } else {
          setTimeLeft("Contest Ended")
          setProgress(100)
        }
      }, 1000)

      return () => clearInterval(interval)
    } else if (contest.status === "upcoming" && contest.startTime) {
      const interval = setInterval(() => {
        const now = new Date().getTime()
        const start = new Date(contest.startTime!).getTime()
        const distance = start - now

        if (distance > 0) {
          const days = Math.floor(distance / (1000 * 60 * 60 * 24))
          const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))
          const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60))

          if (days > 0) {
            setTimeLeft(`${days}d ${hours}h ${minutes}m`)
          } else {
            setTimeLeft(`${hours}h ${minutes}m`)
          }
        } else {
          setTimeLeft("Starting Soon")
        }
      }, 1000)

      return () => clearInterval(interval)
    }
  }, [contest.status, contest.endTime, contest.startTime])

  const handleJoinContest = async () => {
    try {
      // Simulate API call
      await new Promise((resolve) => setTimeout(resolve, 1000))

      setIsJoined(true)
      toast({
        title: "Successfully joined!",
        description: `You've joined ${contest.title}. Good luck!`,
      })
    } catch (error) {
      toast({
        title: "Failed to join contest",
        description: "Please try again later.",
        variant: "destructive",
      })
    }
  }

  const handleContestAction = () => {
    if (contest.status === "active") {
      router.push(`/contest/${contest.id}`)
    } else if (contest.status === "ended") {
      router.push(`/contest/${contest.id}/results`)
    }
  }

  const handleViewLeaderboard = () => {
    router.push(`/contest/${contest.id}/leaderboard`)
  }

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case "Easy":
        return "secondary"
      case "Medium":
        return "default"
      case "Hard":
        return "destructive"
      case "Expert":
        return "destructive"
      default:
        return "secondary"
    }
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case "active":
        return "text-green-400"
      case "upcoming":
        return "text-blue-400"
      case "ended":
        return "text-muted-foreground"
      default:
        return "text-muted-foreground"
    }
  }

  return (
    <Card className="hover:shadow-lg transition-shadow duration-200 border-border">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <CardTitle className="text-lg font-semibold text-balance">{contest.title}</CardTitle>
            <CardDescription className="mt-1 text-pretty">{contest.description}</CardDescription>
          </div>
          <div className="flex flex-col items-end gap-2">
            <Badge variant={getDifficultyColor(contest.difficulty)} className="text-xs">
              {contest.difficulty}
            </Badge>
            <div className={`text-xs font-medium ${getStatusColor(contest.status)}`}>
              {contest.status.charAt(0).toUpperCase() + contest.status.slice(1)}
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        <div className="flex items-center justify-between text-sm text-muted-foreground">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-1">
              <Users className="h-4 w-4" />
              <span>{contest.participants}</span>
              {contest.maxParticipants && <span>/{contest.maxParticipants}</span>}
            </div>
            <div className="flex items-center gap-1">
              <Clock className="h-4 w-4" />
              <span>{contest.duration}</span>
            </div>
          </div>
          {contest.prize && (
            <div className="flex items-center gap-1 text-primary">
              <Trophy className="h-4 w-4" />
              <span className="font-medium">{contest.prize}</span>
            </div>
          )}
        </div>

        {contest.status === "active" && (
          <div className="space-y-2">
            <div className="flex items-center justify-between text-sm">
              <span className="text-muted-foreground">Time Remaining</span>
              <div className="flex items-center gap-1 font-mono text-primary">
                <Timer className="h-3 w-3" />
                {timeLeft}
              </div>
            </div>
            <Progress value={progress} className="h-2" />
          </div>
        )}

        {contest.status === "upcoming" && timeLeft && (
          <div className="flex items-center justify-between text-sm">
            <span className="text-muted-foreground">Starts in</span>
            <div className="flex items-center gap-1 font-mono text-accent">
              <Timer className="h-3 w-3" />
              {timeLeft}
            </div>
          </div>
        )}

        <div className="flex gap-2">
          {contest.status === "upcoming" && (
            <Button className="flex-1" onClick={handleJoinContest} disabled={isJoined}>
              {isJoined ? "Joined" : "Join Contest"}
            </Button>
          )}
          {contest.status === "active" && (
            <Button className="flex-1" variant={isJoined ? "default" : "outline"} onClick={handleContestAction}>
              {isJoined ? "Continue Contest" : "Join Now"}
            </Button>
          )}
          {contest.status === "ended" && (
            <Button className="flex-1 bg-transparent" variant="outline" onClick={handleContestAction}>
              View Results
            </Button>
          )}
          <Button variant="ghost" size="icon" onClick={handleViewLeaderboard}>
            <Trophy className="h-4 w-4" />
          </Button>
        </div>
      </CardContent>
    </Card>
  )
}

components/contest-timer.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Clock, Play, Square } from "lucide-react"
import { cn } from "@/lib/utils"

interface ContestTimerProps {
  startTime: string
  endTime: string
  className?: string
}

export function ContestTimer({ startTime, endTime, className }: ContestTimerProps) {
  const [timeLeft, setTimeLeft] = useState<{
    hours: number
    minutes: number
    seconds: number
    total: number
  }>({ hours: 0, minutes: 0, seconds: 0, total: 0 })

  const [status, setStatus] = useState<"upcoming" | "active" | "ended">("upcoming")

  useEffect(() => {
    const updateTimer = () => {
      const now = new Date().getTime()
      const start = new Date(startTime).getTime()
      const end = new Date(endTime).getTime()

      if (now < start) {
        // Contest hasn't started
        const diff = start - now
        setStatus("upcoming")
        setTimeLeft({
          hours: Math.floor(diff / (1000 * 60 * 60)),
          minutes: Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((diff % (1000 * 60)) / 1000),
          total: diff,
        })
      } else if (now < end) {
        // Contest is active
        const diff = end - now
        setStatus("active")
        setTimeLeft({
          hours: Math.floor(diff / (1000 * 60 * 60)),
          minutes: Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((diff % (1000 * 60)) / 1000),
          total: diff,
        })
      } else {
        // Contest has ended
        setStatus("ended")
        setTimeLeft({ hours: 0, minutes: 0, seconds: 0, total: 0 })
      }
    }

    updateTimer()
    const interval = setInterval(updateTimer, 1000)

    return () => clearInterval(interval)
  }, [startTime, endTime])

  const getStatusIcon = () => {
    switch (status) {
      case "upcoming":
        return <Clock className="h-4 w-4" />
      case "active":
        return <Play className="h-4 w-4" />
      case "ended":
        return <Square className="h-4 w-4" />
    }
  }

  const getStatusColor = () => {
    switch (status) {
      case "upcoming":
        return "bg-blue-100 text-blue-800 border-blue-200"
      case "active":
        return "bg-green-100 text-green-800 border-green-200"
      case "ended":
        return "bg-red-100 text-red-800 border-red-200"
    }
  }

  const getStatusText = () => {
    switch (status) {
      case "upcoming":
        return "Starts in"
      case "active":
        return "Time remaining"
      case "ended":
        return "Contest ended"
    }
  }

  const formatTime = (value: number) => value.toString().padStart(2, "0")

  return (
    <Card className={cn("", className)}>
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-3">
          <Badge className={getStatusColor()}>
            {getStatusIcon()}
            <span className="ml-1">{getStatusText()}</span>
          </Badge>
          {status === "active" &&
            timeLeft.total < 300000 && ( // Last 5 minutes
              <Badge variant="destructive" className="animate-pulse">
                Final Minutes!
              </Badge>
            )}
        </div>

        {status !== "ended" && (
          <div className="flex items-center justify-center gap-2 text-2xl font-mono font-bold">
            <div className="text-center">
              <div
                className={cn(
                  "text-3xl",
                  status === "active" && timeLeft.total < 300000 && "text-red-600 animate-pulse",
                )}
              >
                {formatTime(timeLeft.hours)}
              </div>
              <div className="text-xs text-muted-foreground">HRS</div>
            </div>
            <div className="text-muted-foreground">:</div>
            <div className="text-center">
              <div
                className={cn(
                  "text-3xl",
                  status === "active" && timeLeft.total < 300000 && "text-red-600 animate-pulse",
                )}
              >
                {formatTime(timeLeft.minutes)}
              </div>
              <div className="text-xs text-muted-foreground">MIN</div>
            </div>
            <div className="text-muted-foreground">:</div>
            <div className="text-center">
              <div
                className={cn(
                  "text-3xl",
                  status === "active" && timeLeft.total < 300000 && "text-red-600 animate-pulse",
                )}
              >
                {formatTime(timeLeft.seconds)}
              </div>
              <div className="text-xs text-muted-foreground">SEC</div>
            </div>
          </div>
        )}

        {status === "ended" && (
          <div className="text-center text-muted-foreground">
            <Square className="h-8 w-8 mx-auto mb-2" />
            <p>Contest has concluded</p>
          </div>
        )}
      </CardContent>
    </Card>
  )
}

