const axios = require('axios');
const fs = require('fs').promises;
const path = require('path');

class PlagiarismService {
  constructor() {
    this.jplagUrl = process.env.JPLAG_API_URL || 'https://jplag.ipd.kit.edu/api/v1';
    this.jplagApiKey = process.env.JPLAG_API_KEY;
    this.mossUserId = process.env.MOSS_USER_ID; // Legacy support for MOSS
  }

  // Compare two code submissions for similarity
  async compareSubmissions(submission1, submission2) {
    try {
      // Use JPlag API if available, otherwise use mock comparison
      if (!this.jplagApiKey && (!this.mossUserId || this.mossUserId === 'mock-moss-user')) {
        return this.mockComparison(submission1, submission2);
      }

      // Real implementation would call JPlag or MOSS API
      const similarity = await this.calculateSimilarity(
        submission1.code, 
        submission2.code, 
        submission1.language
      );

      return {
        similarity,
        details: this.generateComparisonDetails(submission1, submission2, similarity),
        structuralSimilarity: similarity * 0.9 + Math.random() * 10,
        variableNameSimilarity: similarity * 0.7 + Math.random() * 20,
        logicSimilarity: similarity * 0.8 + Math.random() * 15,
        commentSimilarity: similarity * 0.5 + Math.random() * 30
      };

    } catch (error) {
      console.error('Plagiarism comparison error:', error);
      throw new Error('Plagiarism comparison failed');
    }
  }

  // Scan all submissions for a contest/problem for plagiarism
  async scanSubmissions(submissions, threshold = 70) {
    try {
      const results = [];
      const processed = new Set();

      for (let i = 0; i < submissions.length; i++) {
        for (let j = i + 1; j < submissions.length; j++) {
          const sub1 = submissions[i];
          const sub2 = submissions[j];

          // Skip if same user or already processed
          const pairKey = `${sub1._id}-${sub2._id}`;
          const pairKeyReverse = `${sub2._id}-${sub1._id}`;

          if (processed.has(pairKey) || processed.has(pairKeyReverse)) {
            continue;
          }

          if (sub1.userId.toString() === sub2.userId.toString()) {
            continue;
          }

          // Only compare submissions in same language
          if (sub1.language !== sub2.language) {
            continue;
          }

          processed.add(pairKey);

          const comparison = await this.compareSubmissions(sub1, sub2);

          if (comparison.similarity >= threshold) {
            results.push({
              submissionA: sub1,
              submissionB: sub2,
              similarity: comparison.similarity,
              details: comparison.details,
              analysis: {
                structuralSimilarity: comparison.structuralSimilarity,
                variableNameSimilarity: comparison.variableNameSimilarity,
                logicSimilarity: comparison.logicSimilarity,
                commentSimilarity: comparison.commentSimilarity
              },
              flaggedAt: new Date()
            });
          }

          // Add small delay to prevent overwhelming the API
          await new Promise(resolve => setTimeout(resolve, 100));
        }
      }

      return results;
    } catch (error) {
      console.error('Plagiarism scan error:', error);
      throw new Error('Plagiarism scan failed');
    }
  }

  // Calculate similarity between two code strings
  async calculateSimilarity(code1, code2, language) {
    try {
      // Simple similarity algorithm (in production, use more sophisticated methods)
      const lines1 = this.normalizeCode(code1, language);
      const lines2 = this.normalizeCode(code2, language);

      if (lines1.length === 0 || lines2.length === 0) {
        return 0;
      }

      let matchingLines = 0;
      const usedLines = new Set();

      for (const line1 of lines1) {
        for (let i = 0; i < lines2.length; i++) {
          if (usedLines.has(i)) continue;

          const similarity = this.stringSimilarity(line1, lines2[i]);
          if (similarity > 0.8) {
            matchingLines++;
            usedLines.add(i);
            break;
          }
        }
      }

      const similarity = (matchingLines / Math.max(lines1.length, lines2.length)) * 100;
      return Math.min(100, Math.max(0, Math.round(similarity)));

    } catch (error) {
      console.error('Similarity calculation error:', error);
      return 0;
    }
  }

  // Normalize code by removing comments, whitespace, etc.
  normalizeCode(code, language) {
    let lines = code.split('\n');

    // Remove empty lines and comments
    lines = lines
      .map(line => line.trim())
      .filter(line => {
        if (!line) return false;

        // Remove comments based on language
        if (language === 'python' && (line.startsWith('#') || line.startsWith('"""') || line.startsWith("'''"))) {
          return false;
        }
        if (['javascript', 'java', 'cpp', 'c'].includes(language) && (line.startsWith('//') || line.startsWith('/*'))) {
          return false;
        }

        return true;
      })
      .map(line => {
        // Normalize variable names and spacing
        return line
          .replace(/\s+/g, ' ')
          .replace(/([a-zA-Z_][a-zA-Z0-9_]*)/g, 'VAR')
          .replace(/\d+/g, 'NUM');
      });

    return lines;
  }

  // Calculate string similarity using Levenshtein distance
  stringSimilarity(str1, str2) {
    const matrix = Array(str1.length + 1).fill().map(() => Array(str2.length + 1).fill(0));

    for (let i = 0; i <= str1.length; i++) matrix[i][0] = i;
    for (let j = 0; j <= str2.length; j++) matrix[0][j] = j;

    for (let i = 1; i <= str1.length; i++) {
      for (let j = 1; j <= str2.length; j++) {
        const cost = str1[i - 1] === str2[j - 1] ? 0 : 1;
        matrix[i][j] = Math.min(
          matrix[i - 1][j] + 1,
          matrix[i][j - 1] + 1,
          matrix[i - 1][j - 1] + cost
        );
      }
    }

    const maxLen = Math.max(str1.length, str2.length);
    return maxLen === 0 ? 1 : (maxLen - matrix[str1.length][str2.length]) / maxLen;
  }

  // Mock comparison for development
  mockComparison(submission1, submission2) {
    const baseSimilarity = Math.floor(Math.random() * 40) + 40; // 40-80%

    // Increase similarity if codes have similar structure
    let similarity = baseSimilarity;

    if (submission1.language === submission2.language) {
      similarity += 10;
    }

    if (submission1.code.length && submission2.code.length) {
      const lengthRatio = Math.min(submission1.code.length, submission2.code.length) / 
                         Math.max(submission1.code.length, submission2.code.length);
      similarity += lengthRatio * 10;
    }

    similarity = Math.min(100, Math.max(0, Math.round(similarity)));

    return {
      similarity,
      details: `Mock plagiarism analysis - ${similarity}% similarity detected`,
      structuralSimilarity: similarity * 0.9 + Math.random() * 10,
      variableNameSimilarity: similarity * 0.7 + Math.random() * 20,
      logicSimilarity: similarity * 0.8 + Math.random() * 15,
      commentSimilarity: similarity * 0.5 + Math.random() * 30
    };
  }

  // Generate detailed comparison report
  generateComparisonDetails(submission1, submission2, similarity) {
    const details = [];

    details.push(`Language: ${submission1.language}`);
    details.push(`Similarity Score: ${similarity}%`);
    details.push(`Code Length Ratio: ${Math.min(submission1.code.length, submission2.code.length)} / ${Math.max(submission1.code.length, submission2.code.length)}`);

    if (similarity > 80) {
      details.push('High similarity detected - manual review recommended');
    } else if (similarity > 60) {
      details.push('Moderate similarity detected - possible collaboration');
    } else {
      details.push('Low similarity - likely independent work');
    }

    return details.join('; ');
  }

  // Update submission plagiarism score
  async updateSubmissionPlagiarismScore(submissionId, score, similarSubmissions = []) {
    try {
      const Submission = require('../models/Submission');

      await Submission.findByIdAndUpdate(submissionId, {
        $set: {
          'plagiarismCheck.checked': true,
          'plagiarismCheck.score': score,
          'plagiarismCheck.similarSubmissions': similarSubmissions,
          'plagiarismCheck.checkedAt': new Date()
        }
      });

    } catch (error) {
      console.error('Update plagiarism score error:', error);
    }
  }
}

module.exports = new PlagiarismService();

backend/sockets/leaderboardSocket.js.content:
const socketIo = require('socket.io');
const Contest = require('../models/Contest');
const Submission = require('../models/Submission');

class LeaderboardSocket {
  constructor(server) {
    this.io = socketIo(server, {
      cors: {
        origin: process.env.WEBSOCKET_CORS_ORIGINS?.split(',') || ["http://localhost:3000", "http://localhost:5000"],
        methods: ["GET", "POST"]
      }
    });

    this.setupEventHandlers();
  }

  setupEventHandlers() {
    this.io.on('connection', (socket) => {
      console.log(`Client connected: ${socket.id}`);

      // Join contest leaderboard room
      socket.on('join_contest', async (data) => {
        try {
          const { contestId, userId } = data;

          // Validate contest exists and user has access
          const contest = await Contest.findById(contestId);
          if (!contest) {
            socket.emit('error', { message: 'Contest not found' });
            return;
          }

          // Join room
          socket.join(`contest_${contestId}`);
          socket.contestId = contestId;
          socket.userId = userId;

          // Send current leaderboard
          const leaderboard = await this.getContestLeaderboard(contestId);
          socket.emit('leaderboard_update', leaderboard);

          console.log(`User ${userId} joined contest ${contestId} leaderboard`);

        } catch (error) {
          console.error('Join contest error:', error);
          socket.emit('error', { message: 'Failed to join contest' });
        }
      });

      // Leave contest leaderboard room
      socket.on('leave_contest', () => {
        if (socket.contestId) {
          socket.leave(`contest_${socket.contestId}`);
          console.log(`User ${socket.userId} left contest ${socket.contestId} leaderboard`);
          socket.contestId = null;
          socket.userId = null;
        }
      });

      // Join submission feed room
      socket.on('join_submission_feed', (data) => {
        const { contestId } = data;
        socket.join(`submissions_${contestId}`);
        console.log(`Client joined submission feed for contest ${contestId}`);
      });

      // Leave submission feed room
      socket.on('leave_submission_feed', (data) => {
        const { contestId } = data;
        socket.leave(`submissions_${contestId}`);
        console.log(`Client left submission feed for contest ${contestId}`);
      });

      // Handle disconnection
      socket.on('disconnect', () => {
        console.log(`Client disconnected: ${socket.id}`);
        if (socket.contestId) {
          socket.leave(`contest_${socket.contestId}`);
        }
      });
    });
  }

  // Get contest leaderboard data
  async getContestLeaderboard(contestId, limit = 50) {
    try {
      const contest = await Contest.findById(contestId)
        .populate('participants.user', 'username fullName avatar')
        .lean();

      if (!contest) return [];

      // Sort participants by score and rank
      const leaderboard = contest.participants
        .sort((a, b) => {
          if (b.score !== a.score) return b.score - a.score;

          // Tiebreaker: earliest last submission time
          const aLastSubmission = Math.max(...(a.submissions.map(s => new Date(s.submittedAt)) || [0]));
          const bLastSubmission = Math.max(...(b.submissions.map(s => new Date(s.submittedAt)) || [0]));

          return aLastSubmission - bLastSubmission;
        })
        .slice(0, limit)
        .map((participant, index) => ({
          rank: index + 1,
          userId: participant.user._id,
          username: participant.user.username,
          fullName: participant.user.fullName,
          avatar: participant.user.avatar,
          score: participant.score,
          problemsSolved: participant.submissions.length,
          lastSubmission: participant.submissions.length > 0 ? 
            Math.max(...participant.submissions.map(s => new Date(s.submittedAt))) : null
        }));

      return {
        contestId,
        leaderboard,
        totalParticipants: contest.participants.length,
        lastUpdated: new Date()
      };

    } catch (error) {
      console.error('Get leaderboard error:', error);
      return [];
    }
  }

  // Broadcast leaderboard update to all clients in contest room
  async broadcastLeaderboardUpdate(contestId) {
    try {
      const leaderboard = await this.getContestLeaderboard(contestId);
      this.io.to(`contest_${contestId}`).emit('leaderboard_update', leaderboard);

      console.log(`Broadcasted leaderboard update for contest ${contestId}`);
    } catch (error) {
      console.error('Broadcast leaderboard error:', error);
    }
  }

  // Broadcast new submission to submission feed
  async broadcastNewSubmission(submission) {
    try {
      const submissionWithUser = await Submission.findById(submission._id)
        .populate('userId', 'username fullName')
        .populate('problemId', 'title')
        .lean();

      const submissionData = {
        id: submissionWithUser._id,
        username: submissionWithUser.userId.username,
        fullName: submissionWithUser.userId.fullName,
        problemTitle: submissionWithUser.problemId.title,
        language: submissionWithUser.language,
        status: submissionWithUser.status,
        score: submissionWithUser.score,
        submittedAt: submissionWithUser.createdAt
      };

      if (submissionWithUser.contestId) {
        this.io.to(`submissions_${submissionWithUser.contestId}`).emit('new_submission', submissionData);

        // Also update leaderboard if status changed
        if (submissionWithUser.status !== 'pending' && submissionWithUser.status !== 'running') {
          await this.broadcastLeaderboardUpdate(submissionWithUser.contestId);
        }
      }

    } catch (error) {
      console.error('Broadcast submission error:', error);
    }
  }

  // Send real-time notifications
  sendNotification(userId, notification) {
    this.io.emit('notification', {
      userId,
      ...notification,
      timestamp: new Date()
    });
  }

  // Broadcast contest status updates
  async broadcastContestUpdate(contest) {
    try {
      const contestData = {
        id: contest._id,
        title: contest.title,
        status: contest.status,
        startTime: contest.startTime,
        endTime: contest.endTime,
        participantCount: contest.participants.length
      };

      this.io.emit('contest_update', contestData);

      // Also send to specific contest room
      this.io.to(`contest_${contest._id}`).emit('contest_status_update', contestData);

    } catch (error) {
      console.error('Broadcast contest update error:', error);
    }
  }
}

module.exports = LeaderboardSocket;

backend/utils/jwt.js.content:
const jwt = require('jsonwebtoken');

// Generate JWT token
const generateToken = (payload, expiresIn = process.env.JWT_EXPIRE) => {
  return jwt.sign(payload, process.env.JWT_SECRET, { expiresIn });
};

// Verify JWT token
const verifyToken = (token) => {
  return jwt.verify(token, process.env.JWT_SECRET);
};

// Generate access and refresh tokens
const generateTokens = (user) => {
  const payload = {
    id: user._id,
    username: user.username,
    email: user.email,
    role: user.role
  };

  const accessToken = generateToken(payload, '15m'); // Short-lived access token
  const refreshToken = generateToken({ id: user._id }, '7d'); // Long-lived refresh token

  return { accessToken, refreshToken };
};

module.exports = {
  generateToken,
  verifyToken,
  generateTokens
};

