export async function GET() {
  try {
    // Simulate API delay
    await new Promise((resolve) => setTimeout(resolve, 500))

    return NextResponse.json({
      success: true,
      data: contests,
    })
  } catch (error) {
    return NextResponse.json({ success: false, error: "Failed to fetch contests" }, { status: 500 })
  }
